window.articles="undefined"!=typeof articles?articles:[];class Blog{constructor(){this.searchInput=document.getElementById("searchInput"),this.articlesGrid=document.getElementById("articlesGrid"),this.noResults=document.getElementById("noResults"),this.filterBtns=document.querySelectorAll(".filter-btn"),this.currentFilter="all",this.currentQuery="",this.init()}init(){this.initNavigation(),this.bindSearchEvents()}initNavigation(){const t=document.getElementById("mobile-menu"),e=document.querySelector(".nav-menu");if(t&&e){t.addEventListener("click",()=>{e.classList.toggle("active"),t.classList.toggle("active")});e.querySelectorAll(".nav-link").forEach(i=>{i.addEventListener("click",()=>{e.classList.remove("active"),t.classList.remove("active")})}),document.addEventListener("click",i=>{t.contains(i.target)||e.contains(i.target)||(e.classList.remove("active"),t.classList.remove("active"))})}}bindSearchEvents(){this.searchInput&&this.searchInput.addEventListener("input",this.debounce(t=>this.handleSearch(t.target.value),300)),this.filterBtns.forEach(t=>{t.addEventListener("click",t=>{this.handleFilter(t.target.getAttribute("data-category"))})})}handleSearch(t){this.currentQuery=t.toLowerCase().trim(),this.renderArticles()}handleFilter(t){this.currentFilter=t,this.filterBtns.forEach(e=>{e.classList.remove("active"),e.getAttribute("data-category")===t&&e.classList.add("active")}),this.renderArticles()}renderArticles(){if(!this.articlesGrid||!window.articles)return;const t=this.filterArticles();0!==t.length?(this.hideNoResults(),this.articlesGrid.innerHTML="",t.forEach(t=>{const e=this.createArticleCard(t);this.articlesGrid.appendChild(e)})):this.showNoResults()}filterArticles(){let t=window.articles||[];return"all"!==this.currentFilter&&(t=t.filter(t=>t.category===this.currentFilter)),this.currentQuery&&(t=t.filter(t=>[t.title,t.summary].join(" ").toLowerCase().includes(this.currentQuery))),t}createArticleCard(t){const e=document.createElement("div");e.className="article-card";const i=`article-category-${t.category}`;return e.innerHTML=`\n            <div class="article-meta">\n                <span class="article-category ${i}">\n                    ${t.category_name}\n                </span>\n                <span class="article-date">${t.time_publish}</span>\n            </div>\n            <h3 class="article-title">\n                <a href="${t.filename}">${t.title}</a>\n            </h3>\n            <p class="article-summary">${t.summary}</p>\n        `,e}showNoResults(){this.articlesGrid&&(this.articlesGrid.innerHTML=""),this.noResults&&(this.noResults.style.display="block")}hideNoResults(){this.noResults&&(this.noResults.style.display="none")}debounce(t,e){let i;return function(...s){clearTimeout(i),i=setTimeout(()=>{clearTimeout(i),t(...s)},e)}}}document.addEventListener("DOMContentLoaded",()=>{new Blog});